<template>
	<view>
		<view class="list">
			<!-- 优惠券列表 -->
			<view class="sub-list goods">
				<view class="row" v-for="(row,index) in collectList" :key="index" >
					<!-- 删除按钮 -->
					<view class="menu" @tap.stop="deleteCoupon(row.id,collectList)">
						<view class="icon shanchu"></view>
					</view>
					<!-- content -->
					<view class="carrier" :class="[typeClass=='goods'?theIndex==index?'open':oldIndex==index?'close':'':'']" @touchstart="touchStart(index,$event)" @touchmove="touchMove(index,$event)" @touchend="touchEnd(index,$event)">
						<view class="goods-info" @tap="toDetail(row.id)">
							<view class="img">
								<image :src="row.mainPicture"></image>
							</view>
							<view class="info">
								<view class="title">{{row.title}}</view>
								<view :class="[row.state==-1?'del':'']" style="font-size: 24upx;padding: 16upx;">
								{{row.state==1?'':(row.state==0?'商品已下架':'商品已被删除')}}
								</view>
								<view class="price-number">
									<view class="keep-num">
										{{row.collectNum}}人收藏
									</view>
									<view class="price">￥{{row.price}}</view>
									
								</view>
							</view>
						</view>
					</view>
				</view>
			</view>
		
		</view>
		<y-prompt id="y-prompt" class="y-prompt" fix="true"></y-prompt>
	</view>
</template>

<script>

	export default {
		data() {
			return {
				collectList:[],//收藏夹列表
				//控制滑动效果
				typeClass:'goods',
				theIndex:null,
				oldIndex:null,
				isStop:false
			}
		},
		onPageScroll(e){
			
		},
		//下拉刷新，需要自己在page.json文件中配置开启页面下拉刷新 "enablePullDownRefresh": true
		onPullDownRefresh() {
		    setTimeout(function () {
		        uni.stopPullDownRefresh();
		    }, 1000);
		},
		onLoad() {
			this.getCollectList();
		},
		methods: {
			getCollectList(){
				this.$postRequest({
					url: 'EShop/getCollect',
					isLoad:false,
					allSuccess: res => {
						this.collectList = res.data;
						if(this.collectList.length==0){
							this.$yPrompt().show({
								title: '收藏夹空空如也',
								msg: '快来添加一个吧'
							});
						}
					},
					allError: () => {
						this.$yPrompt().show({
							title: '加载失败',
							msg: '数据加载失败请重新加载',
							btnTitle: '重新加载',
							isBtn: true,
							btnClick: () => {
								this.getCollectList();
							}
						});
					}
				});
			},
			//控制左滑删除效果-begin
			touchStart(index,event){
				//多点触控不触发
				if(event.touches.length>1){
					this.isStop = true;
					return ;
				}
				this.oldIndex = this.theIndex;
				this.theIndex = null;
				//初始坐标
				this.initXY = [event.touches[0].pageX,event.touches[0].pageY];
			},
			touchMove(index,event){
				//多点触控不触发
				if(event.touches.length>1){
					this.isStop = true;
					return ;
				}
				let moveX = event.touches[0].pageX - this.initXY[0];
				let moveY = event.touches[0].pageY - this.initXY[1];
				
				if(this.isStop||Math.abs(moveX)<5){
					return ;
				}
				if (Math.abs(moveY) > Math.abs(moveX)){
					// 竖向滑动-不触发左滑效果
					this.isStop = true;
					return;
				}
				
				if(moveX<0){
					this.theIndex = index;
					this.isStop = true;
				}else if(moveX>0){
					if(this.theIndex!=null&&this.oldIndex==this.theIndex){
						this.oldIndex = index;
						this.theIndex = null;
						this.isStop = true;
						setTimeout(()=>{
							this.oldIndex = null;
						},150)
					}
				}
			},
			
			touchEnd(index,$event){
				//解除禁止触发状态
				this.isStop = false;
			},
			
			//删除商品
			deleteCoupon(id,List){
				let len = List.length;
				for(let i=0;i<len;i++){
					if(id==List[i].id){
						this.del(id,i);
						break;
					}
				}
				this.oldIndex = null;
				this.theIndex = null;
			},
			
			del(id,i){
				this.$postRequest({
					url: 'EShop/delCollection',
					data:{id:id},
					isLoad:false,
					allSuccess: res => {
						this.collectList.splice(i,1);
						if(this.collectList.length==0){
							this.$yPrompt().show({
								title: '收藏夹空空如也',
								msg: '快来添加一个吧'
							});
						}
					},
					allError: () => {
						uni.showToast({
							icon:'Error',
							title:'删除失败'
						});
					}
				});
			},
			
			toDetail(id){
				
			},
			
			discard() {
				//丢弃
			}
			
			
		}
	}
</script>
